mapper.select:
SELECT *
FROM travelinfo


		AND title LIKE CONCAT('%', #{params.title}, '%')


		AND infotype = #{params.infotype}


		AND city_id = #{params.cityId}


ORDER BY created_at DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM travelinfo


		AND title LIKE CONCAT('%', #{params.title}, '%')


		AND infotype = #{params.infotype}


		AND city_id = #{params.cityId}

SELECT ti.idx, ti.title, ti.content, ti.infotype, ti.infodate, ti.member_id, c.id AS city_id, c.name AS city_name, co.id AS country_id, co.name AS country_name FROM travelinfo ti JOIN city c ON ti.city_id = c.id JOIN country co ON c.country_id = co.id
AND ti.title LIKE CONCAT('%', #{params.title}, '%')
AND ti.infotype = #{params.infotype}
AND ti.city_id = #{params.cityId}
ORDER BY ti.created_at DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM travelinfo ti JOIN city c ON ti.city_id = c.id JOIN country co ON c.country_id = co.id
AND ti.title LIKE CONCAT('%', #{params.title}, '%')
AND ti.infotype = #{params.infotype}
AND ti.city_id = #{params.cityId}
